import React from 'react';
import {
  FaDribbbleSquare,
  FaFacebookSquare,
  FaGithubSquare,
  FaInstagram,
  FaTwitterSquare,
} from 'react-icons/fa';

const Footer = () => {
  return (
    <div className='flex justify-between items-center h-16 px-4 text-white z-50 relative bg-white w-full'> {/* Changed to w-full and removed max-w-[1240px] and mx-auto */}
      <div className='max-w-[1240px] mx-auto w-full flex justify-between items-center'> {/* This is the new div that wraps the content */}
        <h1 className='w-full text-3xl font-bold text-[#5A3D33]'>BOKDOL.</h1>
        <ul className='hidden md:flex'>
          <li className='text-[#5A3D33] p-4'>Home</li>
          <li className='text-[#5A3D33] p-4'>Whitepaper</li>
        </ul>
        <div onClick={handleNav} className='block md:hidden'>
          {nav ? <AiOutlineClose size={20} /> : <AiOutlineMenu size={20} />}
        </div>
      </div>
      <ul className={nav ? 'fixed left-0 top-0 w-[60%] h-full border-r border-r-gray-900 bg-[#000300] ease-in-out duration-500 z-50' : 'ease-in-out duration-500 fixed left-[-100%] z-50'}>
        <h1 className='w-full text-3xl font-bold text-[#00df9a] m-4'>REACT.</h1>
        <li className='p-4 border-b border-gray-600'>Home</li>
        <li className='p-4'>Whitepaper</li>
      </ul>
    </div>
  );
};

export default Footer;
